mapper.select:

select * from
(select qa_no,id,qa_title,qa_content,qa_date,qa_open, rownum r
from (select * from qa order by qa_no desc)
order by qa_no desc)where r >= #{start} and r <= #{end}

select * from
(select qa_no,id,qa_title,qa_content,qa_date,qa_open, rownum r
from (select * from qa order by qa_date desc)



		qa_title like '%'||#{search}||'%'
		 qa_content like '%'||#{search}||'%' 
		 id like '%'||#{search}||'%' 


order by qa_date desc 

) where r >= #{start} AND r <= #{end}
